class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n=nums.size();

        bool ch=false;
        int curr=0;
        map<long long,long long>mp;
        int prev=-1;
        for(int i=0;i<n;i++){
            map<long long,long long>mp1;
            mp1[nums[i]]=1;
            if(nums[i]==k)
            ans++;
            for(auto it: mp){
                long long nt=it.first&nums[i];
                if(nt==k)
                ans+=it.second;
                mp1[nt]+=it.second;
            }
            mp=mp1;

        }
        return ans;
    }
};
