class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int s = array.size();
        long long ttl = 0;
        unordered_map<long long, long long> pResults;
        for (int index = 0; index < s; ++index) {
            unordered_map<long long, long long> cResults; 
            if (array[index] == target) {
                ttl++;
            }
            cResults[array[index]] = 1;
            for (auto& [value, frequency] : pResults) {
                long long newAndResult = value & array[index];
                if (newAndResult == target) {
                   ttl += frequency;
                }
                cResults[newAndResult] += frequency;
            }

            pResults = cResults;
        }
        return ttl;
    }
};
