class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        long long count = 0;
        unordered_map<int, int> andMap;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> newAndMap;

            for (auto& [key, value] : andMap) {
                int newAnd = key & arr[i];
                newAndMap[newAnd] += value;
            }

            newAndMap[arr[i]]++;

            for (auto& [key, value] : newAndMap) {
                if (key == k) {
                    count += value;
                }
            }

            andMap = newAndMap;
        }

        return count;
    }
};
