class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long res = 0;

        unordered_map<int,int> tmp;

        for(int i=0;i<n;i++){

            if(nums[i] == k) res++;

            unordered_map<int,int> tmp2;
            tmp2[nums[i]] = 1;

            for(auto it : tmp){
                int t=it.first & nums[i];
                if(t==k){
                    res += it.second;
                }

                tmp2[t] += it.second;
            }
            tmp = tmp2;
        }

        return res;
    }

};
