class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        start = [[nums[0], 0, 0]]
        n = len(nums)
        answer = 0
        if k==nums[0]:
            answer+=1
        for i in range(1, n):
            next_s = [[nums[i], i, i]]
            for x, l, r in start:
                x = x&nums[i]
                if x != next_s[-1][0]:
                    next_s.append([x, float('inf'), -1*float('inf')])
                next_s[-1][1] = min(next_s[-1][1], l)
                next_s[-1][2] = max(next_s[-1][2], r)
            start= next_s
            m =len(start)
           # print(start)
            for i2 in range(m):
                a, l, r= start[i2]
                #print(a, l,r)
                if a==k:
                    answer+=(r-l+1)
        return answer
