using ll = long long;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans = 0;
        unordered_map<int,int> s;
        int n = nums.size();
        if(nums[0]==k) ++ans;
        s[nums[0]]++;
        for(int i = 1;i<n;i++)
        {
            unordered_map<int,int> mp;
            for(auto & [v,f]:s)
            {
                mp[v&nums[i]]+=f;
            }
            mp[nums[i]]++;
            ans+=mp[k];
            s.swap(mp);
        }
        return ans;
    }
};
