class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        cnt = 0
        mp = {}
        for i in range(n):
            cur_mp = {}
            if nums[i] == k:
                cnt += 1
            cur_mp[nums[i]] = 1

            for key in mp:
                res = key & nums[i]
                if res == k:
                    cnt += mp[key]
                cur_mp[res] = cur_mp.get(res, 0) + mp[key]

            mp = cur_mp
        return cnt
