class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long int count = 0;
        int n = nums.size();
        unordered_map<int, int> MP;

        for (int start = 0; start < n; start++) {
            unordered_map<int, int> MP1;
            if (nums[start]== k )
                { count++; }
            MP1[nums[start]] = 1;
            for (auto i : MP) {
                long long addition = i.first & nums[start];
                if (addition == k) {
                    count += i.second;
                }
                MP1[addition]+=i.second;
            }
            MP = MP1;
        }

        return count;
    }
};
