class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0; 
        unordered_map<long long, long long> prefix;
        long long n = nums.size();
        for(int i = 0; i < n; i++) 
        { 
            if(nums[i] == k) 
            {
                ans++;
            }
            unordered_map<long long, long long> current;
            current[nums[i]] = 1;
            for(auto& a : prefix) 
            {
                long long freq = a.second;
                long long temp = a.first & nums[i];
                if(temp == k) 
                {
                    ans += freq;
                }
                current[temp] += freq;
            }
            prefix = current;
        }
        return ans;
    }
};
