class Solution:
    # time O(nlogM), space O(n)
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = 0
        pre = {}
        for x in nums:
            res += x == k
            cur = {x: 1}
            for val, cnt in pre.items():
                and_val = val & x
                if and_val == k:
                    res += cnt
                cur[and_val] = cur.get(and_val, 0) + cnt
            pre = cur
        return res
