class Solution {
public:
    long long countSubarrays(vector<int>& array, int tgt) {
        int size = array.size();
        long long ttl = 0;
        unordered_map<long long, long long> pvsRslt;
        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> crtRslt; 
            if (array[i] == tgt) {
                ++ttl;
            }
            crtRslt[array[i]] = 1;
            for (auto& [value, frequency] : pvsRslt) {
                long long newRslt = value & array[i];
                if (newRslt == tgt) {
                    ttl += frequency;
                }
                crtRslt[newRslt] += frequency;
            }

            pvsRslt = crtRslt;
        }
        return ttl;
    }
};
