class Solution {
public:
             long long pb = 0;
            std::unordered_map<long long, long long> pr;
            int i=0;
    long long countSubarrays(vector<int>& nums, int k) {
         int m = nums.size();
            while(i<m){
            std::unordered_map<long long, long long> cr;
            if (nums[i] == k)++pb;
            cr[nums[i]] = 1;
            for (const auto& [vl, fr] : pr) {
                long long nr = vl & nums[i];
                if (nr == k) {
                    pb += fr;
                }
                cr[nr] += fr;
            }

            pr = cr;
                i++;
            }

        return pb;
    }
};
