class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    long long count = 0;
    int n = nums.size();
    unordered_map<int, int> mp;

    for (int i = 0; i < n; ++i) {
        unordered_map<int, int> mpp;
        mpp[nums[i]]++;
        for (auto& entry : mp) {
            int nd = entry.first & nums[i];
            mpp[nd] += entry.second;
        }

        if (mpp.find(k) != mpp.end()) {
            count += mpp[k];
        }

        mp = mpp;
    }

    return count;
    }
};
