class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long int ans = 0;
        map<long long int , long long int > pre;
        for (int index = 0; index < n; ++index) {
            map<long long int , long long int > cur; 
            if (nums[index] == k) {
                ++ans;
            }
            cur[nums[index]] = 1;
            for (auto& [value, frequency] : pre) {
                long long int  newAndResult = value & nums[index];
                if (newAndResult == k) {
                    ans += frequency;
                }
                cur[newAndResult] += frequency;
            }
            pre=cur;
        }
        return ans;
    }
};
