class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> pR;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cRs;
            if (nums[i] == k) {
                ++cnt;
            }
            int sum=0;
            cRs[nums[i]] = 1;
            for (auto& [val, freq] : pR) {
                long long nar = val & nums[i];
                if (nar == k) {
                    cnt += freq;
                }
                cRs[nar] += freq;
            }
            //fiinal ans


            pR = cRs;
        }

        return cnt;
    }
};
