class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        tb = [0] * 31 
        ans = 0
        for i, num in enumerate(nums):
            if k != 0:
                includes, others = inf, -inf
                for d in range(31):
                    tb[d] = tb[d] + 1 if (num >> d) & 1 else 0
                    if (k >> d) & 1:
                        includes = min(includes, tb[d])
                    else:
                        others = max(others, tb[d])

                if includes > others:
                    ans += includes - others 

            else:
                mx = 0
                for d in range(31):
                    tb[d] = tb[d] + 1 if (num >> d) & 1 else 0
                    mx = max(mx, tb[d])

                ans += i + 1 - mx

        return ans
