class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k)
    {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> shannu1;
        long long req2=0;
        for (int i = 0; i < n; ++i)
        {
            unordered_map<long long, long long> shannu2; 
            if (nums[i] != k)
            {
                req2++;
            }
            else
            {
                ans++;
            }
            long long req1=0;
            shannu2[nums[i]] = 1;
            for (auto ptr=shannu1.begin();ptr!=shannu1.end();ptr++)
            {
                long long newans = (ptr->first) & nums[i];
                if (newans != k)
                {
                    req1++;
                }
                else
                {
                    ans += ptr->second;
                }
                shannu2[newans] += ptr->second;
            }
            shannu1 = shannu2;
        }
        return ans;

    }
};
