class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();

        long long ans = 0;

        unordered_map<long long, long long> mp;

        for(int i=0;i<n;i++){
            unordered_map<long long, long long> curr;

            for(auto [ansScore, freq]: mp){
                long long score = ansScore & nums[i];

                if(score == k){
                    ans += freq;
                }

                curr[score] += freq;
            }

            curr[nums[i]]++;
            if(nums[i] == k) ans++;

            mp = curr;
        }

        return ans;
    }
};
