class Solution {
public:
    long long solve(vector<int>& nums, int k) {
        int size = nums.size();
        long long ans = 0;

        unordered_map<long long, long long> prev;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> curr; 
            if (nums[index] == k) {
                ans++;
            }
            curr[nums[index]] = 1;
            for (auto& [value, frequency] : prev) {
                long long next = value & nums[index];
                if (next == k) {
                    ans += frequency;
                }
                curr[next] += frequency;
            }

            prev = curr;
        }
        return ans;

    }


    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums, k);
    }
};
