class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<long long, long long> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr;

            if (nums[i] == k) {
                ++cnt;
            }

            curr[nums[i]] = 1;

            for (auto& [pv, pf] : prev) {
                long long newAnd = pv & nums[i];

                if (newAnd == k) {
                    cnt += pf;
                }

                curr[newAnd] += pf;
            }

            prev = curr;
        }

        return cnt;
    }
};
