class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();

        vector<unordered_map<long long, long long>> dp(n);
        for (int i = 0; i < n; i++) {
            if (nums[i] == k) {
                ans++;
            }

            dp[i][nums[i]] = 1;
            if (i != 0) {
                for (auto it: dp[i-1]) {
                    long long val = it.first & nums[i];
                    if (val == k) {
                        ans += it.second;
                    }

                    dp[i][val] += it.second;
                }

                dp[i-1] = dp[i];
            }
        }

        return ans;
    }
};
