class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
    vector<vector<int>>mp(32);
    int n=a.size();
    long long ans=0;
    for(int i=0;i<n;i++){
        for(int j=0;j<31;j++){
            if(((a[i]>>j)&1)==0){
                mp[j].push_back(i);
            }
        }
    }
   //if(!mp[0].empty()) cout<<mp[0][0]<<endl;
    for(int i=0;i<n;i++){
        int mini=1e9;
            int cur_and=a[i];
    int cur=i;
    int next_index=-1;
    while(1){
        int mini=n;
        for(int j=0;j<31;j++){
            if((cur_and>>j)&1){
            int x=lower_bound(mp[j].begin(),mp[j].end(),cur+1)-mp[j].begin();
            if(x==mp[j].size()) continue;
             mini=min(mini,mp[j][x]);}
        }
        if(cur_and==k) ans+=mini-cur;

        if(mini==n) break;
        cur_and=cur_and&a[mini];
        cur=mini;
        if(cur_and<k || mini==n) break;
    }
    //cout<<ans<<endl;
    }
    return ans;
    }
};
