class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;

        unordered_map<int,int>mp;
        for(int i=0;i<n;i++){
            unordered_map<int,int>mp2;
            if(nums[i]==k){
                ans++;
            }
            mp2[nums[i]]=1;
            for(auto j:mp){
                long long x=j.first & nums[i];
                if(x==k){
                    ans+=j.second;
                }
                mp2[x]+=j.second;;
            }
            mp=mp2;
        }
        return ans;
    }
};
