class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<long long, long long> dp;  // dp[and_value] = count of subarrays with this AND value
        long long count = 0;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> newDp;
            if (nums[i] == k) count++;
            newDp[nums[i]]++;

            for (const auto& [and_value, freq] : dp) {
                long long newAndValue = and_value & nums[i];
                if (newAndValue == k) {
                    count += freq;
                }
                newDp[newAndValue] += freq;
            }
            dp = newDp;
        }

        return count;
    }
};
