class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int last_req_zero = -1;
        vector<int> last_nr_zero(32, -1);
        long long res = 0;
        for (int i = 0; i < nums.size(); i++) {
            int cv = nums[i];
            if ((cv & k) != k) {
                last_req_zero = i;
            }
            int span_start = last_req_zero+1;
            int span_end = i;
            for (int j = 0; j < 31; j++) {
                if ((k & (1<<j)) == 0) {
                    if ((cv & (1<<j)) == 0) {
                        last_nr_zero[j] = i;
                    }
                    span_end = min(span_end, last_nr_zero[j]);
                }
            }
            res += max(0, span_end-span_start+1);
        }
        return res;
    }
};
