#define ll long long
class Solution {
public:
    ll ans;
    void solve(int idx, int k, unordered_map<ll,ll>&mp, unordered_map<ll,ll>&mpp){
        if(idx==k) ans++;
        mpp[idx]=1;

        for(auto &pp:mp){
            if((pp.first & idx)==k) ans+=pp.second;
            mpp[pp.first&idx]+=pp.second;
        }
        mp=mpp;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        ll n= nums.size();
        ans=0;
        unordered_map<ll,ll>mp;
        for(auto &it:nums){
            unordered_map<ll,ll>mpp;
            solve(it,k,mp,mpp);
        }
        return ans;
    }
};
