class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt = 0;
        unordered_map<int, long long> andMap;
        int val = 0;

        for (int num : nums) {
            val = num;
            if (val == k) {
                cnt++;
            }
            unordered_map<int, long long> newMap;
            for (auto& [key, v] : andMap) {
                int nval = key & num;
                if (nval == k) {
                    cnt += v;
                }
                newMap[nval] += v;
            }
            newMap[val]++;
            andMap = newMap;
        }

        return cnt;
    }
};
