class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        map<long long,long> prev;
        for(auto it:nums){
            map<long long,long long> mpp;
            if(it==k) ans++;
            mpp[it]=1;
            for(auto i:prev){
                long long val=i.first,freq=i.second;
                long long curr=val&it;
                if(curr==k){
                    ans+=freq;
                }
                mpp[curr]+=freq;
            }
            prev.clear();
            for(auto it:mpp){
                prev.insert(it);
            }
        }
        return ans;
    }
};
