class Solution {
public:
    long long countSubarrays(vector<int>& b, int k) {
        long long n=b.size();
        long long a[n+1][32];
        memset(a,0,sizeof(a));
        for (int i=1; i<=n; i++)
        {
            long long x=b[i-1];
            long long j=0;
            while (x)
            {
                a[i][j]=x%2;
                x/=2;
                j++;
            }
            for (int j=0; j<32; j++) a[i][j]+=a[i-1][j];
        }
        long long ans=0;
        for (int i=1; i<=n; i++)
        {
            long long lo = 1, hi = i;
        while (lo < hi)
        {
            long long mid = (lo + hi) / 2;
            long long val = 0;
            for (int j = 0; j < 32; j++)
            {
                if ((a[i][j] - a[mid - 1][j]) == i - mid + 1)
                    val += (1 << j);
            }
            if (val < k)
                lo = mid + 1;
            else
                hi = mid;
        }
        long long val = 0;
        for (int j = 0; j < 32; j++)
        {
            if (a[i][j] - a[lo - 1][j] == i - lo + 1)
                val += (1 << j);
        }
        if (val == k)
        {
            long long lo1 = 1, hi1 = i;
            while (lo1 < hi1)
            {
                long long mid = (lo1 + hi1 + 1) / 2;
                long long v = 0;
                for (int j = 0; j < 32; j++)
                {
                    if (a[i][j] - a[mid - 1][j] == i - mid + 1)
                        v += (1 << j);
                }
                if (v <= k)
                    lo1 = mid;
                else
                    hi1 = mid - 1;
            }

            long long d = lo1 - lo + 1;
            ans += d;
        }
        }
        return ans;



    }
};
