class Solution {
public:
     long long countSubarrays(vector<int>& v, int t) {
        int n = v.size();
        long long ans = 0;
        unordered_map<long long, long long> dp;
        vector<int> a(n);
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> temp; 
            if (v[i] == t) ++ans;
            a[i]=i+1;
            temp[v[i]] = 1;
            for (auto it : dp) {
                long long curr = it.first & v[i];
                if (curr == t) {
                    ans += it.second;
                }
                a[i]++;
                temp[curr] += it.second;
            }
            dp = temp;
        }
        return ans;
    }
};
