class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<int, int> dp;
        for (int i = 0; i < n; i++) {
            unordered_map<int, int> new_dp;
            new_dp[nums[i]]++;
            if (nums[i] == k) count++;

            for (auto& [key, val] : dp) {
                int new_key = key & nums[i];
                new_dp[new_key] += val;
                if (new_key == k) count += val;
            }

            dp = new_dp;
        }

        return count;
    }
};
