class Solution {
public:

    long long countSubarrays(vector<int>& nums, int k) {

    long long n = nums.size();
    unordered_map<long long, long long> dp;
    long long count = 0;

    for (long long i = 0; i < n; i++) {
        unordered_map<long long, long long> new_dp;

        if (nums[i] == k) {
            count++;
        }

        new_dp[nums[i]] = 1;

        for (auto& [andValue, freq] : dp) {
            long long new_and = andValue & nums[i];
            if (new_and == k) {
                count += freq;
            }
            new_dp[new_and] += freq;
        }

        dp = new_dp;
    }

    return count;
    }
};
