class Solution {

    boolean ff(int[][] b,int l,int r){

        int su = r-l+1;
        for(int i=0;i<32;i++){
           // System.out.println(b[r][1]-(l==0?0:b[l-1][1]));
            if(b[r][i]-(l==0?0:b[l-1][i]) ==su)
            {

           // System.out.println("false");
                return false;
            }
        }
        System.out.println("true");
        return true;
    }

    public long fun(List<Integer> lis){
        int j=0;
        long ans=0;
        int[][] b= new int[lis.size()][32]; 
        for(int i=0;i<lis.size();i++){
            for(int k=0;k<32;k++){
                b[i][k] = (lis.get(i)&(1<<k))!=0?1:0;
                if(i!=0)
                b[i][k]+=b[i-1][k];
            }

            while(ff(b,j,i)){
                j++;
            }
            System.out.println(j+" ");
            ans+=j;
        }

        return ans;
    }
    public long countSubarrays(int[] nums, int k) {
        List<Integer> l = new ArrayList<>();
        long ans=0;
        for(int i=0;i<nums.length;i++){
            if((nums[i]&k)<k)
            {
                System.out.println(i+" "+l.size());
                if(l.size()>0)
                ans+=fun(l);
                l.clear();
            }
            else
            l.add(nums[i]-k);
        }
        if(l.size()>0)
        ans+=fun(l);
        return ans;

    }
}
