class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        map<long long ,long long> mp;
        long long ans=0;
        for(int i=0;i<n;i++){
            map<long long,long long> mp2;
            if(nums[i]==k){
                ans++;
            }
            mp2[nums[i]]++;
            for(auto x:mp){
                if((nums[i]&x.first)==k){
                    ans+=x.second;
                }
                mp2[(nums[i]&x.first)]+=x.second;
            }
            mp=mp2;
        }
        return ans;
    }
};
