class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    unordered_map<int, int> andMap;
    long long count = 0;

    for (int i = 0; i < n; ++i) {
        unordered_map<int, int> newMap;
        for (auto& [val, freq] : andMap) {
            int newAnd = val & nums[i];
            if (newAnd == k) {
                count += freq;
            }
            newMap[newAnd] += freq;
        }
        newMap[nums[i]]++;
        if (nums[i] == k) {
            count++;
        }
        andMap = move(newMap);
    }

    return count;
    }
};
