using ll = long long;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n=nums.size();

        if(k==0){
            ll yolo=0;
            vector<vector<ll>> dp(n,vector<ll>(30,0));
        for(int j=0;j<30;j++){
            if((1<<j)&nums[0]) dp[0][j]=1;
        }
        if(nums[0]==0) yolo++;
        for(int i=1;i<n;i++){
            for(int j=0;j<30;j++){
                if((1<<j)&nums[i]) dp[i][j]=dp[i-1][j]+1;
                else dp[i][j]=0;
            }
            ll res=i+1;
            ll res2=0;
            for(int j=0;j<30;j++){
                if((1<<j)&k);
                else res2=max(res2,dp[i][j]);
            }
            res=res-res2;
            res=max(res,0ll);
            yolo=yolo+res;
        }
            return yolo;
        }

        vector<vector<ll>> dp(n,vector<ll>(30,0));
        for(int j=0;j<30;j++){
            if((1<<j)&nums[0]) dp[0][j]=1;
        }
        ll mn=1;
        for(int j=0;j<30;j++){
            if((1<<j)&k) mn=min(mn,dp[0][j]);
        }
        if(nums[0]==k) ans++;
        for(int i=1;i<n;i++){
            for(int j=0;j<30;j++){
                if((1<<j)&nums[i]) dp[i][j]=dp[i-1][j]+1;
                else dp[i][j]=0;
            }
            ll res=n;
            for(int j=0;j<30;j++){
                if((1<<j)&k) res=min(res,dp[i][j]);
            }
            ll res2=0;
            for(int j=0;j<30;j++){
                if((1<<j)&k);
                else res2=max(res2,dp[i][j]);
            }
            res=res-res2;
            res=max(res,0ll);
            ans=ans+res;
            //cout<<ans<<' ';
        }
        return ans;
    }
};
