class Solution {
public:
    int fn(map<int,int>& mp,int len){

        int x = 0;
        for(int i = 0 ; i < 31 ; ++i){
            if(mp[i]==len)
                x|=(1<<i);
        }
        return x;

    }

    bool cont(int a,int k){
        for(int i = 0 ; i < 31 ; ++i){
                if((k>>i)&1)
                    if(!((a>>i)&1))
                        return false;
            }
        return true;
    }

    long long ffn(vector<int>& nums, int k){

        int l = 0 , j = 0 , n = nums.size() ;
        long long ans = 0;

        map<int,int> mp;
        while(j<n){


                for(int i = 0 ; i < 31 ; ++i){
                    if((nums[j]>>i)&1)
                        mp[i]++;
                }


            while(fn(mp,j-l+1)<k){

                for(int i = 0 ; i < 31 ; ++i){
                    if((nums[l]>>i)&1)
                        mp[i]--;
                }
                l++;

            }

            ans+=j-l+1;
            j++;
        }

        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {

        return ffn(nums,k)-ffn(nums,k+1);
    }
};
