class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int n = array.size();
        long long sum = 0;
        unordered_map<int, long long> count;
        for (int i = 0; i < n; i++) {
            unordered_map<int, long long> curr;
            array[i] == target ? sum = sum + 1 : sum = sum;
            curr[array[i]] += 1;
            for (auto& kv : count) {
                int and_val = kv.first & array[i];
                if (and_val == target) {
                    sum += kv.second;
                }
                curr[and_val] += kv.second;
            }
            count.swap(curr);
        }

        return sum;
    }
};
