class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long result = 0;
        unordered_map<long long, long long> a;

        for (int b = 0; b < n; ++b) {
            unordered_map<long long, long long> c;

            if (nums[b] == k) {
                ++result;
            }

            c[nums[b]] = 1;

            for (auto& [d, e] : a) {
                long long f = d & nums[b];
                if (f == k) {
                    result += e;
                }
                c[f] += e;
            }

            a = c;
        }

        return result;
    }
};
