class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n = v.size();
        long long c = 0;
        unordered_map<long long, long long> p;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> m;

            if (v[i] == k) {
                ++c;
            }
            m[v[i]] = 1;

            for (auto x : p) {
                auto a = x.first;
                auto b = x.second;
                long long r = a & v[i];
                if (r == k) {
                    c += b;
                }
                m[r] += b;
            }

            p = move(m);
        }

        return c;
    }
};
