class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
     int n=nums.size();
     unordered_map<long long , long long>mp;
     long long res=0;

     for(int i=0;i<n;i++){
        unordered_map<long long ,long long >mp1;
        for(auto &it: mp){
            long long val=it.first & nums[i];
            mp1[val]+=it.second;
        }
        mp1[nums[i]]++;
        mp=mp1;
        if(mp.find(k)!=mp.end()) res+=mp[k];
     }
     return res;
    }
};
