class Solution {
    public long countSubarrays(int[] nums, int k) {
        HashMap<Long, Long> hm = new HashMap<>();
          long javab = 0;


        for (int i = 0; i < nums.length; i++) {
            HashMap<Long, Long> hm2 = new HashMap<>();
            hm2.put((long) nums[i], 1L);
            if (nums[i] == k) {
                javab++;
            }
            int num = nums[i];

            for (Map.Entry<Long, Long> entry : hm.entrySet()) {

                long ele = entry.getKey() & num;

                if (ele == k) {
                    javab += entry.getValue();
                }
                long key = hm2.getOrDefault(ele, 0L);
                hm2.put(ele,  key+ entry.getValue());
            }

            hm = hm2;
        }

        return javab;
    }
}
