class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> mp;
        multiset<int>st;
        stack<int>stk;
            for(int i=0;i<1000;i++) stk.push(i); 

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> mp2;


            if (nums[i] == k) {
                ++cnt;
            }
            mp2[nums[i]] = 1;
            st.insert(nums[i]);

            for (auto& [val, freq] : mp) {
                long long res = val & nums[i];
                if (res == k) {
                    cnt += freq;
                }
                st.insert(freq);
                mp2[res] += freq;
                st.erase(res);
            }

            mp = mp2;
        }
        while(stk.size()) stk.pop();

        return cnt;
    }
};
