class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> mpp;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> currMpp; 
            if (nums[i] == k) {
                ans++;
            }
            currMpp[nums[i]] = 1;
            for (auto& [key, val] : mpp) {
                long long newans = key & nums[i];
                if (newans == k) {
                    ans += val;
                }
                currMpp[newans] += val;
            }

            mpp = currMpp;
        }
        return ans;
    }
};
