class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long int, int> pre;
        // long long int current_and = 0;
        long long int ans = 0;
        for (int num : nums) {
            unordered_map<long long int,int> curr;
            if(num==k){
                ans++;
                // curr[num]++;
            }
            curr[num]=1;
            for(auto& i : pre){
                int u=(i.first)&(num);
                if(u==k){
                    ans+=i.second;
                    // curr[u]+=i.ss;
                }
                curr[u]+=i.second;
            }
            pre=curr;
        }
        return ans;
    }
};
