class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int m = arr.size();
        long long cnt = 0;

        unordered_map<long long, long long> mp;

        for (int i = 0; i < m; ++i) {
            unordered_map<long long, long long> c; 

            if (arr[i] == k) {
                cnt++;
            }
            c[arr[i]] = 1;

            for (auto& [val, freq] : mp) {
                long long j= val & arr[i];
                if (j == k) {
                    cnt += freq;
                }
                c[j] += freq;
            }

            mp = c;
        }

        return cnt;
    }
};
