class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         int n = nums.size();
        long long dixit = 0;

        unordered_map<long long, long long> p;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curran; 

            if (nums[i] == k) {
                ++dixit;
            }
            curran[nums[i]] = 1;

            for (auto& [v, f] : p) {
                long long kohli = v & nums[i];
                if (kohli == k) {
                    dixit += f;
                }
                curran[kohli] += f;
            }

            p = curran;
        }

        return dixit;
    }
};
