class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
    long long count = 0;
    unordered_map<int, int> freqMap;

    for (int i = 0; i < n; ++i) {
        unordered_map<int, int> newFreqMap;
        for (auto& pair : freqMap) {
            int newAnd = pair.first & nums[i];
            newFreqMap[newAnd] += pair.second;
        }
        newFreqMap[nums[i]]++;

        freqMap = newFreqMap;

        if (freqMap.find(k) != freqMap.end()) {
            count += freqMap[k];
        }
    }

    return count;
    }
};
