class Solution(object):
    def countSubarrays(self, nums, k):
        n = len(nums)
        cnt=0
        arr={}

        for i in range(n):
            val={}

            if nums[i] == k:
                cnt+=1
            val[nums[i]] = 1

            for ans, res in arr.items():
                saw = ans & nums[i]
                if saw == k:
                    cnt += res
                if saw in val:
                    val[saw] += res
                else:
                    val[saw] = res

            arr = val

        return cnt;
