class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ios::sync_with_stdio(false); // Optimize I/O operations
        cin.tie(nullptr); // Untie cin from cout for faster I/O
       int size = nums.size();
        long long total = 0;
        vector<int>v;
        unordered_map<long long, long long> previousResults;
        for (int index = 0; index < size; index++) {
            unordered_map<long long, long long> currentResults; 
            if (nums[index] == k) {
                total++;
                v.push_back(total);
            }
            currentResults[nums[index]] = 1;
            for (auto& [value, frequency] : previousResults) {
                long long newAndResult = value & nums[index];
                if (newAndResult == k) {
                    total += frequency;
                    v.push_back(total);
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }
        return total;
    }
};
